import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Update = () => {
    const [user_id, setUser_id] = useState('');
    const [description, setDescription] = useState('');
    const [name, setName] = useState('');
    const [city, setCity] = useState('');
    const [cpostal, setCpostal] = useState('');
    const [address, setAddress] = useState('');
    const [siren, setSiren] = useState('');
    const [type_studio, setType_studio] = useState('');
    const [img_son, setImg_son] = useState('');
    const [prix, setPrix] = useState('');
    const [nbr_people, setNbr_people] = useState('');
    const [tva, setTva] = useState('');


    var data = {
        'name': name ,
        'user_id': user_id ,
        'description':description,
        'city' :city,
        'cpostal': cpostal,
        'address': address,
        'siren': siren,
        'type_studio': type_studio,
        'img_son': img_son,
        'prix': prix,
        'nbr_people': nbr_people,
        'tva':tva,



    }

    const validate = (e) => {
        axios.post('', data, {

        }).then(function (response) {
            console.log(response);
        })
            .catch(function (error) {
                console.log(error);
            });
    };

    return (
        <div className="container">
            <div className="card text-center">
                <div className="card-body">
                    <h1>S'inscrire</h1>

                    <div className="form-group">
                        <label>Name :</label>
                        <input type="text"
                            className="form-control"
                            placeholder="Name"
                            required
                            onChange={(event) => setName(event.target.value)}></input>
                    </div>
                    <div className="form-group">
                        <label>user :</label>
                        <input type="text"
                            className="form-control"
                            placeholder="Prenom"
                            required
                            onChange={(event) => setUser_id(event.target.value)}></input>
                    </div>

                    <div className="form-group">
                        <label>Description :</label>
                        <input type="text"
                            className="form-control"
                            placeholder="Description"
                            required
                            onChange={(event) => setDescription(event.target.value)}></input>
                    </div>
                    <div className="form-group">
                        <label>Password :</label>
                        <input type="text"
                            className="form-control"
                            placeholder="Password"
                            required
                            onChange={(event) => setCity(event.target.value)}></input>
                    </div>
                    <div className="form-group">
                        <label>choix :</label>
                        <input type="radio"
                            className="form-control"
                            placeholder="Choix"
                            required
                            onChange={(event) => setCpostal(event.target.value)}></input>
                    </div>
                    <div className="form-group">
                        <label>Adresse :</label>
                        <input type="radio"
                            className="form-control"
                            placeholder="Choix"
                            required
                            onChange={(event) => setAddress(event.target.value)}></input>
                    </div>
                    <div className="form-group">
                        <label>Siren :</label>
                        <input type="radio"
                            className="form-control"
                            placeholder="Siren"
                            required
                            onChange={(event) => setSiren(event.target.value)}></input>
                    </div>

                    <Link onClick={validate}>
                        <button type="submit"
                            className="btn btn-primary">Submit</button>
                    </Link>
                    <button><a href="/login">Login</a></button>
                </div>
            </div>
        </div>
    )
}

export default Update;
